  async putTA({ response, request, auth, params: { ta_id } }) {
    const domain = request.headers().origin;

    const sekolah = await this.getSekolahByDomain(domain);

    if (sekolah == "404") {
      return response.notFound({ message: "Sekolah belum terdaftar" });
    }

    const user = await auth.getUser();

    if (
      user.role != "admin" ||
      user.role == "guru" ||
      user.m_sekolah_id != sekolah.id
    ) {
      return response.forbidden({ message: messageForbidden });
    }

    const {
      tahun,
      semester,
      nama_kepsek,
      nip_kepsek,
      aktif,
      tanggal_awal,
      tanggal_rapor,
      tanggal_akhir,
      tingkat1,
      tingkat2,
      tingkat3,
      tingkat4,
      tingkat5,
      tingkat6,
    } = request.post();
    const rules = {
      tahun: "required",
      semester: "required",
      nama_kepsek: "required",
      nip_kepsek: "required",
      aktif: "required",
    };
    const message = {
      "tahun.required": "Tahun harus diisi",
      "semester.required": "Semester  harus diisi",
      "nama_kepsek.required": "Nama Kepsek harus dipilih",
      "nip_kepsek.required": "NIP Kepsek harus dipilih",
      "aktif.required": "Status harus dipilih",
    };
    const validation = await validate(request.all(), rules, message);
    if (validation.fails()) {
      return response.unprocessableEntity(validation.messages());
    }

    if (aktif == 1) {
      await Mta.query()
        .where({ m_sekolah_id: sekolah.id })
        .andWhere({ dihapus: 0 })
        .update({
          aktif: 0,
        });
    }

    const ta = await Mta.query().where({ id: ta_id }).update({
      tahun,
      semester,
      nama_kepsek,
      nip_kepsek,
      aktif,
      tanggal_awal,
      tanggal_rapor,
      tanggal_akhir,
      tingkat1,
      tingkat2,
      tingkat3,
      tingkat4,
      tingkat5,
      tingkat6,
    });

    if (!ta) {
      return response.notFound({
        message: messageNotFound,
      });
    }

    return response.ok({
      message: messagePutSuccess,
    });
  }

