  async postPost({ response, request, auth }) {
    const domain = request.headers().origin;

    const sekolah = await this.getSekolahByDomain(domain);

    if (sekolah == "404") {
      return response.notFound({ message: "Sekolah belum terdaftar" });
    }

    const user = await auth.getUser();

    const { judul, konten, banner, m_kategori_id, simpan } = request.post();
    const rules = {
      judul: "required",
      konten: "required",
      banner: "required",
    };
    const message = {
      "judul.required": "Judul harus diisi",
      "banner.required": "Banner harus diisi",
      "konten.required": "Konten harus diisi",
    };
    const validation = await validate(request.all(), rules, message);
    if (validation.fails()) {
      return response.unprocessableEntity(validation.messages());
    }

    const slug = `${slugify(judul)}-${Date.now()}`;

    let post;

    if (simpan) {
      post = await MPost.create({
        judul,
        slug,
        konten: konten,
        banner,
        disembunyikan: 1,
        dihapus: 0,
        m_user_id: user.id,
        m_sekolah_id: sekolah.id,
      });
    } else {
      post = await MPost.create({
        judul,
        slug,
        konten: konten,
        banner,
        disembunyikan: 0,
        dihapus: 0,
        m_user_id: user.id,
        m_sekolah_id: sekolah.id,
      });
    }

    let tkPostData = [];

    if (m_kategori_id) {
      await Promise.all(
        m_kategori_id.map((data) => {
          tkPostData.push({
            m_kategori_id: data,
            m_post_id: post.id,
          });
        })
      );

      await TkPost.createMany(tkPostData);
    }

    if (simpan) {
      return response.ok({
        message: messageSaveSuccess,
      });
    }

    return response.ok({
      message: messagePostSuccess,
    });
  }

