  async downloadPendaftarPPDB({ response, request, auth }) {
    // Create a document
    const doc = new PDFDocument({ size: "A4" });

    let namaFile = `/uploads/kartu-peserta-${Date.now()}.pdf`;

    // Pipe its output somewhere, like to a file or HTTP response
    // See below for browser usage
    doc.pipe(fs.createWriteStream(`public${namaFile}`));

    // Embed a font, set the font size, and render some text
    // doc
    //   .font("fonts/PalatinoBold.ttf")
    //   .fontSize(25)
    //   .text("Some text with an embedded font!", 100, 100);

    doc.fontSize(25).text("Some text with an embedded font!", 100, 100);
    // ini masih buffer
    axios
      .get(
        "https://awsimages.detik.net.id/community/media/visual/2020/07/10/tes-psikologi.jpeg",
        { responseType: "arraybuffer" }
      )
      .then((response) => {
        const pngBuffer = Buffer.from(response.data);
        doc.image(pngBuffer);
      });

    // Add an image, constrain it to a given size, and center it vertically and horizontally
    // doc.image(image, {
    //   fit: [250, 300],
    //   align: "center",
    //   valign: "center",
    // });

    // Add another page
    doc
      .addPage()
      .fontSize(25)
      .text("Here is some vector graphics...", 100, 100);

    // Draw a triangle
    doc
      .save()
      .moveTo(100, 150)
      .lineTo(100, 250)
      .lineTo(200, 250)
      .fill("#FF3300");

    // Apply some transforms and render an SVG path with the 'even-odd' fill rule
    doc
      .scale(0.6)
      .translate(470, -380)
      .path("M 250,75 L 323,301 131,161 369,161 177,301 z")
      .fill("red", "even-odd")
      .restore();

    // Add some text with annotations
    doc
      .addPage()
      .fillColor("blue")
      .text("Here is a link!", 100, 100)
      .underline(100, 100, 160, 27, { color: "#0000FF" })
      .link(100, 100, 160, 27, "http://google.com/");

    // Finalize PDF file
    doc.end();

    return "sukses";
  }

