      "bank.required": "Bank harus diisi",
      "norek.required": "Norek harus diisi",
      "nama_pemilik.required": "Nama pemilik harus diisi",
      "nominal.required": "Nominal harus diisi",
      "bukti.required": "Bukti harus diisi",
