  async postAplikasiSesbro({ response, request }) {
    try {
      const { aplikasi } = request.post();

      const existingAplikasi = await MSesbro.findBy("name", aplikasi);

      if (existingAplikasi) {
        return response.status(400).send({
          message: "Aplikasi with the same name already exists",
          error: true,
        });
      }

      const aplikasiName = await MSesbro.create({ name: aplikasi });

      return response.ok({
        message: "Aplikasi created successfully",
        data: aplikasiName,
      });
    } catch (error) {
      console.error(error);
      return response
        .status(500)
        .send({ message: "Internal server error", error: true });
    }
  }

  async daftarsekolah({ response, request }) {
    const { search, page, limit } = request.get();

    let sekolah = MSekolah.query().select(
      "id",
      "nama",
      "favicon",
      "domain",
      "lat",
      "long"
    );

    if (search) {
      sekolah.where("nama", "like", `%${search}%`);
    }

    if ((page, limit)) {
      sekolah = await sekolah.forPage(page, limit).fetch();
    } else {
      sekolah = await sekolah.limit(50).fetch();
    }

    return sekolah;
  }

  async searchSekolahKolaborasi({ response, request, auth }) {
    const { search, page, limit, bentuk } = request.get();

    let sekolah = MSekolah.query().select("id", "nama", "favicon", "domain");

    if (search) {
      sekolah.where("nama", "like", `%${search}%`);
    }

    if (bentuk) {
      sekolah.where({ tingkat: bentuk });
    }

    if ((page, limit)) {
      sekolah = await sekolah.forPage(page, limit).fetch();
    } else {
      sekolah = await sekolah.limit(50).fetch();
    }

    const bentukData = await MSekolah.query()
      .select("tingkat")
      .whereNotNull("tingkat")
      .distinct("tingkat")
      .fetch();

    return { sekolah, bentuk: bentukData };
  }

