  async detailRombelRapor({
    response,
    request,
    auth,
    params: { user_id, mata_pelajaran_id },
  }) {
    const domain = request.headers().origin;

    const sekolah = await this.getSekolahByDomain(domain);

    if (sekolah == "404") {
      return response.notFound({ message: "Sekolah belum terdaftar" });
    }
    const ta = await this.getTAAktif(sekolah);

    const user = await auth.getUser();

    const ta_id = user?.m_ta_id ? user.m_ta_id : ta.id;

    const rombelIds = await MRombel.query()
      .where({ m_sekolah_id: sekolah.id })
      .andWhere({ dihapus: 0 })
      .andWhere({ m_ta_id: ta_id })
      .ids();

    const siswa = await User.query()
      .with("anggotaRombel", (builder) => {
        builder
          .with("rombel")
          .where({ dihapus: 0 })
          .whereIn("m_rombel_id", rombelIds);
      })
      .where({ id: user_id })
      .first();

    const mapelSingkat = await MMataPelajaran.query()
      .where({ id: mata_pelajaran_id })
      .first();

    const mapel = await MMataPelajaran.query()
      .with("user")
      .with("materi", (builder) => {
        builder
          .where({ tingkat: siswa.toJSON().anggotaRombel.rombel.tingkat })
          .andWhere({ dihapus: 0 });
        if (sekolah.id == 578 || ta_id == 121) {
          if (mapelSingkat.kelompok == "C") {
            if (siswa.toJSON().anggotaRombel.rombel.m_jurusan_id != null) {
              builder.andWhere({
                m_jurusan_id: siswa.toJSON().anggotaRombel.rombel.m_jurusan_id,
              });
            }
          }
        }
      })
      .where({ id: mata_pelajaran_id })
      .first();

    const rekap = await TkRekapNilai.query()
      .with("rekapRombel", (builder) => {
        builder.with("rekap", (builder) => {
          builder
            .where({ tipe: "tugas" })
            .andWhere({ m_ta_id: ta_id })
            .andWhere({ dihapus: 0 })
            .andWhere({ m_materi_id: mapel.toJSON().materi.id });
        });
      })
      .where({ m_user_id: user_id })
      .fetch();

    const rekapUjian = await TkRekapNilai.query()
      .with("rekapRombel", (builder) => {
        builder.with("rekap", (builder) => {
          builder
            .where({ tipe: "ujian" })
            .andWhere({ teknik: "UH" })
            .andWhere({ m_ta_id: ta_id })
            .andWhere({ dihapus: 0 })
            .andWhere({ m_materi_id: mapel.toJSON().materi.id });
        });
      })
      .where({ m_user_id: user_id })
      .fetch();

    const { nilaiAkhir, ujian, rata, rataUjian, data, dataUjian } =
      await HitungNilaiAkhir({
        rekap,
        rekapUjian,
        mapel,
        user_id,
        ta,
        sekolah,
      });
    // const dataUjian1 =
    //   result1.reduce((a, b) => a.nilai + b, 0) / result1.length;

    return response.ok({
      data,
      rata,
      dataUjian,
      rataUjian,
      nilaiAkhir,
      ujian,
      siswa,
      mapel,
    });
  }

