  async putKeteranganRapor({ response, request, auth, params: { user_id } }) {
    const domain = request.headers().origin;

    const sekolah = await this.getSekolahByDomain(domain);

    if (sekolah == "404") {
      return response.notFound({ message: "Sekolah belum terdaftar" });
    }

    const { catatan, kelulusan, sakit, izin, alpa, tipe } = request.post();
    const rules = {
      kelulusan: "required",
    };
    const message = {
      "kelulusan.required": "Kekelulusan harus diisi",
    };
    const validation = await validate(request.all(), rules, message);
    if (validation.fails()) {
      return response.unprocessableEntity(validation.messages());
    }

    const keteranganRapor = await MKeteranganRapor.query()
      .where({ m_user_id: user_id })
      .where({ tipe })
      .update({
        catatan,
        kelulusan,
        dihapus: 0,
        sakit,
        izin,
        alpa,
      });

    if (!keteranganRapor) {
      return response.notFound({
        message: messageNotFound,
      });
    }

    return response.ok({
      message: messagePutSuccess,
    });
  }

