  async downloadCatatanKelulusan({
    response,
    request,
    auth,
    params: { rombel_id },
  }) {
    const domain = request.headers().origin;

    const sekolah = await this.getSekolahByDomain(domain);

    if (sekolah == "404") {
      return response.notFound({ message: "Sekolah belum terdaftar" });
    }

    const ta = await this.getTAAktif(sekolah);

    const user = await auth.getUser();

    let { tipe, ta_id = ta.id } = request.post();

    const keluarantanggalseconds = moment().format("YYYY-MM-DD ") + Date.now();

    const anggotaRombel = await MAnggotaRombel.query()
      .with("user", (builder) => {
        builder.select("id", "nama", "whatsapp");
      })
      .where({ m_rombel_id: rombel_id })
      .andWhere({ dihapus: 0 })
      .fetch();

    let workbook = new Excel.Workbook();
    let worksheet = workbook.addWorksheet(`sheet1`);
    worksheet.mergeCells("A1:E1");
    worksheet.mergeCells("A2:E2");
    worksheet.getCell("A1").value = "Import Keterangan Kelulusan";
    worksheet.getCell("A2").value = sekolah.nama;
    worksheet.getCell("H4").value = "Catatan : ";
    worksheet.getCell("H4").value =
      "Keterangan diisi dengan lulus atau tidak lulus";
    worksheet.getCell(
      "A3"
    ).value = `Diunduh tanggal ${keluarantanggalseconds} oleh ${user.nama}`;
    worksheet.addConditionalFormatting({
      ref: "A1:E2",
      rules: [
        {
          type: "expression",
          formulae: ["MOD(ROW()+COLUMN(),1)=0"],
          style: {
            font: {
              name: "Times New Roman",
              family: 4,
              size: 16,
              bold: true,
            },
            // fill: {
            //   type: "pattern",
            //   pattern: "solid",
            //   bgColor: { argb: "C0C0C0", fgColor: { argb: "C0C0C0" } },
            // },
            alignment: {
              vertical: "middle",
              horizontal: "center",
            },
            // border: {
            //   top: { style: "thin" },
            //   left: { style: "thin" },
            //   bottom: { style: "thin" },
            //   right: { style: "thin" },
            // },
          },
        },
      ],
    });
    worksheet.addConditionalFormatting({
      ref: "A4:E4",
      rules: [
        {
          type: "expression",
          formulae: ["MOD(ROW()+COLUMN(),1)=0"],
          style: {
            font: {
              name: "Times New Roman",
              family: 4,
              size: 12,
              bold: true,
            },
            fill: {
              type: "pattern",
              pattern: "solid",
              bgColor: { argb: "C0C0C0", fgColor: { argb: "C0C0C0" } },
            },
            alignment: {
              vertical: "middle",
              horizontal: "center",
            },
            border: {
              top: { style: "thin" },
              left: { style: "thin" },
              bottom: { style: "thin" },
              right: { style: "thin" },
            },
          },
        },
      ],
    });
    await Promise.all(
      anggotaRombel
        .toJSON()
        .sort((a, b) => ("" + a.user.nama).localeCompare(b.user.nama))
        .map(async (d, idx) => {
          const keterangan = await MKeteranganRapor.query()
            .where({ dihapus: 0 })
            .andWhere({ tipe: tipe })
            .andWhere({ m_ta_id: ta_id })
            .andWhere({ m_user_id: d.m_user_id })
            .first();

          const user = await User.query().where({ id: d.m_user_id }).first();

          worksheet.addConditionalFormatting({
            ref: `B${(idx + 1) * 1 + 4}:E${(idx + 1) * 1 + 4}`,
            rules: [
              {
                type: "expression",
                formulae: ["MOD(ROW()+COLUMN(),1)=0"],
                style: {
                  font: {
                    name: "Times New Roman",
                    family: 4,
                    size: 11,
                    // bold: true,
                  },
                  alignment: {
                    vertical: "middle",
                    horizontal: "left",
                  },
                  border: {
                    top: { style: "thin" },
                    left: { style: "thin" },
                    bottom: { style: "thin" },
                    right: { style: "thin" },
                  },
                },
              },
            ],
          });
          worksheet.addConditionalFormatting({
            ref: `A${(idx + 1) * 1 + 4}`,
            rules: [
              {
                type: "expression",
                formulae: ["MOD(ROW()+COLUMN(),1)=0"],
                style: {
                  font: {
                    name: "Times New Roman",
                    family: 4,
                    size: 11,
                    // bold: true,
                  },
                  alignment: {
                    vertical: "middle",
                    horizontal: "center",
                  },
                  border: {
                    top: { style: "thin" },
                    left: { style: "thin" },
                    bottom: { style: "thin" },
                    right: { style: "thin" },
                  },
                },
              },
            ],
          });

          // add column headers
          worksheet.getRow(4).values = [
            "No",
            "Nama",
            "Whatsapp",
            "Catatan",
            "Keterangan",
          ];
          worksheet.columns = [
            { key: "no" },
            { key: "nama" },
            { key: "whatsapp" },
            { key: "catatan" },
            { key: "kelulusan" },
          ];

          // Add row using key mapping to columns
          let row = worksheet.addRow({
            no: `${idx + 1}`,
            nama: user ? user.nama : "-",
            whatsapp: user ? user.whatsapp : "-",
            catatan: keterangan ? keterangan.catatan : "",
            kelulusan: keterangan ? keterangan.kelulusan : "",
          });
        })
    );
    let namaFile = `/uploads/rekap-Catatan-Kelulusan-${keluarantanggalseconds}.xlsx`;

    // save workbook to disk
    await workbook.xlsx.writeFile(`public${namaFile}`);

    return namaFile;
  }

  async deleteAnggotaEkskul({
    response,
    request,
    auth,
    params: { ekskul_id },
  }) {
    const domain = request.headers().origin;

    const sekolah = await this.getSekolahByDomain(domain);

    if (sekolah == "404") {
      return response.notFound({ message: "Sekolah belum terdaftar" });
    }

    const ta = await this.getTAAktif(sekolah);

    if (ta == "404") {
      return response.notFound({ message: "Tahun Ajaran belum terdaftar" });
    }

    const user = await auth.getUser();

    if (
      user.role != "admin" ||
      user.role == "guru" ||
      user.m_sekolah_id != sekolah.id
    ) {
      return response.forbidden({ message: messageForbidden });
    }

    const ekskul = await MAnggotaEkskul.query()
      .where({ id: ekskul_id })
      .update({
        dihapus: 1,
      });

    if (!ekskul) {
      return response.notFound({
        message: messageNotFound,
      });
    }

    return response.ok({
      message: messageDeleteSuccess,
    });
  }

